import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const RunAndHitHold = ({ playerId, pos }) => {
  const [player, setPlayer] = React.useState(null);

  useEffect(() => {
    const grabPerson = () =>
    fetch(`api/players`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedPlayers) => {
        const personInList = fetchedPlayers.filter(player => player.id == playerId);
        setPlayer(personInList[0]);
      })

      grabPerson();

    }, [playerId]);
  if (player != null) {
    if (pos == null) {
      return (
        <Col>
          <strong>{player.name}</strong>
        </Col>
      );
    }
    return (
      <Col>
        <strong>{player.name}</strong>
        <small>{player.position}</small>
      </Col>

    );
  }
  return (<div>Loading...</div>);



}

const InningReader = ({ currInning, hitOrder }) => {
  const [balls, setBalls] = React.useState(currInning ? currInning.balls : 0);
  const [strikes, setStrikes] = React.useState(currInning ? currInning.strikes : 0);
  const [outs, setOuts] = React.useState(currInning ? currInning.outs : 0);
  const [frID, setFirstRunner] = React.useState(currInning ? currInning.firstRunnerId : 0);
  const [srID, setSecondRunner] = React.useState(currInning ? currInning.secondRunnerId : 0);
  const [trID, setThirdRunner] = React.useState(currInning ? currInning.thirdRunnerId : 0);

  const [bf, setFaced] = React.useState(0);
  const [whatHitter, setNumber] = React.useState(hitOrder);

  const [hitter, setHitter] = React.useState(0);
  const [hitPos, setHitPos] = React.useState(0)
  const [pitcher, setPitcher] = React.useState(0);

  const [escucha, setEscucha] = React.useState(false);

  function noOneOn(number) {
    if (number == 0) {
      return false;
    }
    return true;
  }

  function grabHitter(lineupObj, number) {
    if (number == 0) {
      return [lineupObj.firstHitter, lineupObj.firstPos];
    }
    else if (number == 1) {
      return [lineupObj.secondHitter, lineupObj.secondPos];
    }
    else if (number == 2) {
      return [lineupObj.thirdHitter, lineupObj.thirdPos];
    }
    else if (number == 3) {
      return [lineupObj.fourthHitter, lineupObj.fourthPos];
    }
    else if (number == 4) {
      return [lineupObj.fifthHitter, lineupObj.fifthPos];
    }
    else if (number == 5) {
      return [lineupObj.sixthHitter, lineupObj.sixthPos];
    }
    else if (number == 6) {
      return [lineupObj.seventhHitter, lineupObj.seventhPos];
    }
    else if (number == 7) {
      return [lineupObj.eighthHitter, lineupObj.eighthPos];
    }
    else if (number == 8) {
      return [lineupObj.ninthHitter, lineupObj.ninthPos];
    }
    else {
      setNumber(0);
      return [lineupObj.firstHitter, lineupObj.firstPos];
    }

  }

  useEffect(() => {
    const firstStep = () =>
    fetch(`api/lineups/${currInning.gameId}`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedLineups) => {
        const hittersLineup = fetchedLineups.filter(lineup => currInning.hittingId == lineup.teamId);
        const pitchersLineup = fetchedLineups.filter(lineup => currInning.pitchingId == lineup.teamId);
        setPitcher(pitchersLineup[0].pitcher);
        setHitter(grabHitter(hittersLineup[0], whatHitter));
      })

      firstStep();

    }, []);

  //Speech Packaging
  var indexSpeech = 0;
  var SpeechRecognition = window.webkitSpeechRecognition;
  var recognition = new SpeechRecognition();
  recognition.continuous = false;
  recognition.onstart = function() {
    console.log("We are listening. Try speaking into the microphone.");
  };
  recognition.onend = function() {
    recognition.abort();
  }
  recognition.onerror = function() {
    console.log("Error");
  }

  recognition.onresult = function(event) {
    var transcript = event.results[indexSpeech][0].transcript;
    var confidence = event.results[indexSpeech][0].confidence;
    indexSpeech += 1;
    console.log(transcript);
    console.log(confidence);
};

  const listeningButton = (
    <Button onClick={() => {
      if (escucha == false) {
        recognition.start();
        setEscucha(true);
      }
      else {
        recognition.abort();
        console.log("Speech has stopped");
        setEscucha(false);
      }
      }}>Test</Button>
  );
  //End of Speech Packaging

  if (currInning == null) {
    return(<div>Loading...</div>);
  }

  return (
    <div>
      <Container>
        <Row>
          <Col>
            <strong>Balls:</strong>
            <small>{balls}</small>
          </Col>
          <Col>
            <strong>Strikes:</strong>
            <small>{strikes}</small>
          </Col>
          <Col>
            <strong>Outs:</strong>
            <small>{outs}</small>
          </Col>
        </Row>
        <Row>
          <Col>
            <div>Pitcher</div>
          </Col>
          <RunAndHitHold playerId={pitcher} pos={null}></RunAndHitHold>
        </Row>
        <Row>
          <Col>
            <div>Hitter</div>
          </Col>
          <RunAndHitHold playerId={hitter} pos={hitPos}></RunAndHitHold>
        </Row>
        {!noOneOn(frID) && <Row>
                        <Col>
                          <div>On first...</div>
                        </Col>
                        <RunAndHitHold playerId={frID} pos={null}></RunAndHitHold>
                      </Row>}
        {!noOneOn(srID) && <Row>
                        <Col>
                          <div>On second...</div>
                        </Col>
                        <RunAndHitHold playerId={srID} pos={null}></RunAndHitHold>
                      </Row>}
        {!noOneOn(trID) && <Row>
                        <Col>
                          <div>On third...</div>
                        </Col>
                        <RunAndHitHold playerId={trID} pos={null}></RunAndHitHold>
                      </Row>}
      </Container>
    </div>
  );

}

export default InningReader;
