import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

import classes from "./ButtonArea.module.css";

const ButtonArea = ({ access, team, returnHome, setTeamState }) => {
  const addPlayers = (
    <button
      onClick={() => setTeamState("Players")}>Add Players
    </button>
  );
  const addScorers = (
    <button
      onClick={() => setTeamState("Scorers")}>Add Scorers
    </button>
  );
  const addFans = (
    <button
      onClick={() => setTeamState("Fans")}>Add Fans
    </button>
  );
  const goHome = (
    <button
      onClick={() => returnHome()}>Go Home
    </button>
  );
  const startGame = (
    <button
      onClick={() => setTeamState("Game")}>Start Game!
    </button>
  );
  //const seeStatsTeam = () => {};
  //const seeStatsDivision = () => {};
  if (access.type == 1) {
    return (<div className={classes.ButtonArea}>
      {addPlayers} {addFans} {startGame} {goHome}
    </div>);

  }
  else if (access.type == 2) {
    return (
      <div className={classes.ButtonArea}>
        {goHome}
      </div>
    );

  }
  else {
    return (
      <div className={classes.ButtonArea}>
        {goHome}
      </div>
    );

  }




}

export default ButtonArea;
