import React, { useState, useEffect } from "react";
import "./App.css";

import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

import TeamAccessHolder from "./components/TeamAccessHolder"
import LoginScreen from "./components/LoginScreen"
import TeamList from "./components/TeamList"


const Header = styled.h1`
  background-color: blue;
  text-align: center;
  padding: 1rem;
  color: white;
`;
const ButtonDisplay = styled.div`
  background-color: red;
  padding: 0.5rem 0;
  margin-bottom: 0.5rem;
`;

const Message = styled.div`
  background-color: white;
  padding: 70px 0;
  text-align: center;
  color: black;
  /*background-image: url('Jefferson.png')*/
`;

const App = () => {
  const [currUser, setUser] = React.useState(null);
  const [access, setAccess] = React.useState(null);
  const [signinError, setError] = React.useState("");

  const [mode, setMode] = React.useState("Home");

  console.log(currUser);

  useEffect(() => {
      const fetchUser = () => {
        fetch("/api/users")
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedUsers) => {
            // Create a copy of the collections, replacing the current article if this is an edit
            const checkedSignedIn = fetchedUsers.filter(user => user.signedIn == 1);
            if (checkedSignedIn.length != 0) {
              setUser(checkedSignedIn[0]);
              const myID = checkedSignedIn[0].id;
              console.log("ID HERE");
              console.log(myID);
              return myID;
            }
          }).then((thatID) => {
            if (thatID != null) {
              fetch(`/api/teamaccess`)
                .then((response) => {
                  if (response.ok) {
                    return response.json();
                  }
                  throw new Error(response.statusText);
                })
                .then((fetchedAccesses) => {

                    const filteredAccess = fetchedAccesses.filter(point => point.userId == thatID);

                  // Create a copy of the collections, replacing the current article if this is an edit
                    setAccess(filteredAccess);
                  })
                .catch((err) => console.error(err)); // eslint-disable-line no-console
            }
          })
          .catch((err) => console.error(err)); // eslint-disable-line no-console
      };

      fetchUser();

    }, [setUser]);

  console.log(access);

  const handleSignInReturn = (username, password) => {
    fetch("/api/users")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedUsers) => {
        // Create a copy of the collections, replacing the current article if this is an edit
        const checkedSignedIn = fetchedUsers.filter(user => user.username == username && user.password == password);
        if (checkedSignedIn.length != 0) {

          const newPerson = {
            id: checkedSignedIn[0].id,
            username: checkedSignedIn[0].username,
            password: checkedSignedIn[0].password,
            signedIn: 1
          }

          const updatedPerson = {...checkedSignedIn[0], ...newPerson};

          fetch(`/api/users/${updatedPerson.id}`, {
            method: "PUT",
            body: JSON.stringify(updatedPerson),
            headers: new Headers({
              Accept: "application/json",
              "Content-Type": "application/json"
            })
          })
            .then(response => {
              if (response.ok) {
                return response.json();
              }
              console.log(response);
              throw new Error(response.statusText);

            })
            //Add the newly created order to local orders and update order state
            .then((finished) => {
              setUser(finished);
              setError("");

            })
            .catch(err => {
              console.error(err);
            });
        }
        else {
          setError("Your username/password combination do not match the ones found in our database");
        }
      })
      .catch((err) => console.error(err)); // eslint-disable-line no-console
  };

  const handleSignOutReturn = () => {
    const newPerson = {
      id: currUser.id,
      username: currUser.username,
      password: currUser.password,
      signedIn: 0
    }

    console.log(newPerson);
    const updatedPerson = {...currUser, ...newPerson};
    console.log(updatedPerson);

    fetch(`/api/users/${updatedPerson.id}`, {
      method: "PUT",
      body: JSON.stringify(updatedPerson),
      headers: new Headers({
        Accept: "application/json",
        "Content-Type": "application/json"
      })
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        console.log(response);
        throw new Error(response.statusText);

      })
      //Add the newly created order to local orders and update order state
      .then((finished) => {
        setUser(null);
        setAccess(null);
        setError("");
        setMode("Home");

      })
      .catch(err => {
        console.error(err);
      });


  }

  const handleAccountCreation = (personCredentials) =>
    {
      fetch("/api/users")
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        })
        .then((fetchedUsers) => {
          // Create a copy of the collections, replacing the current article if this is an edit
          const checkedSignedIn = fetchedUsers.filter(user => user.username == personCredentials.username);
          if (checkedSignedIn.length != 0) {
            setError("Someone with this username already exists! Either sign-in to your other account or try a new username");
          }
          else {
            fetch("/api/users", {
              method: "POST",
              body: JSON.stringify(personCredentials),
              headers: new Headers({
                Accept: "application/json",
                "Content-Type": "application/json"
              })
            })
              .then(response => {
                if (response.ok) {
                  return response.json();
                }
                throw new Error(response.statusText);
              })
              //Add the newly created order to local orders and update order state
              .then(receivedPerson => {
                setUser(receivedPerson);
              })
              .catch(err => {
                console.error(err);
              });
          }
        })
        .catch((err) => console.error(err));  // eslint-disable-line no-console
    };


  if (currUser == null) {
    return (
      <div>
        <Header>Voice-Activated Baseball Scoring</Header>
        <Container>
          <LoginScreen signIn={handleSignInReturn} createAccount={handleAccountCreation} errorMessage={signinError}></LoginScreen>
        </Container>
      </div>
    );
  }


  return (

    <div>
      <Header>Voice-Activated Baseball Scoring</Header>
      <Button onClick={handleSignOutReturn}>Sign Out!</Button>
      <Message>Thanks for signing-in! Please select one of your teams below to get started</Message>
      <TeamList access={access}></TeamList>
    </div>
  );

};

export default App;
