import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const RunAndHitHold = ({ playerId, pos }) => {
  const [player, setPlayer] = React.useState(null);

  console.log(playerId);

  useEffect(() => {
    const grabPerson = () =>
    fetch(`api/players`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedPlayers) => {
        const personInList = fetchedPlayers.filter(player => player.id == playerId);
        setPlayer(personInList[0]);
      })

      grabPerson();

    }, [playerId, pos]);
  if (player != null) {
    if (pos == null) {
      return (
        <Col>
          <strong>{player.name}</strong>
        </Col>
      );
    }
    return (
      <Col>
        <strong>{player.name}</strong>
        <small>{pos}</small>
      </Col>

    );
  }
  return (<div>Loading...</div>);

}

const InningReader = ({ currInning, hitOrder }) => {
  const [balls, setBalls] = React.useState(currInning ? currInning.balls : 0);
  const [strikes, setStrikes] = React.useState(currInning ? currInning.strikes : 0);
  const [outs, setOuts] = React.useState(currInning ? currInning.outs : 0);
  const [frID, setFirstRunner] = React.useState(currInning ? currInning.firstRunnerId : 0);
  const [srID, setSecondRunner] = React.useState(currInning ? currInning.secondRunnerId : 0);
  const [trID, setThirdRunner] = React.useState(currInning ? currInning.thirdRunnerId : 0);

  const [bf, setFaced] = React.useState(0);
  const [whatHitter, setNumber] = React.useState(hitOrder);

  const [hitterInfo, setHitterInfo] = React.useState(0);
  const [pitcher, setPitcher] = React.useState(0);

  //Recording Hooks
  const [buttonText, setButtonText] = React.useState("Not Listening...");
  const [buttonColor, setButtonColor] = React.useState("#eb020e");
  const [lastPlay, setLastPlay] = React.useState("Game Started");
  const [speakingError, setMessage] = React.useState("");

  function noOneOn(number) {
    if (number == 0) {
      return false;
    }
    console.log("Why???");
    return true;
  }

  function grabHitter(lineupObj, number) {
    if (number == 0) {
      return [lineupObj.firstId, lineupObj.firstPos];
    }
    else if (number == 1) {
      return [lineupObj.secondId, lineupObj.secondPos];
    }
    else if (number == 2) {
      return [lineupObj.thirdId, lineupObj.thirdPos];
    }
    else if (number == 3) {
      return [lineupObj.fourthId, lineupObj.fourthPos];
    }
    else if (number == 4) {
      return [lineupObj.fifthId, lineupObj.fifthPos];
    }
    else if (number == 5) {
      return [lineupObj.sixthId, lineupObj.sixthPos];
    }
    else if (number == 6) {
      return [lineupObj.seventhId, lineupObj.seventhPos];
    }
    else if (number == 7) {
      return [lineupObj.eighthId, lineupObj.eighthPos];
    }
    else if (number == 8) {
      return [lineupObj.ninthId, lineupObj.ninthPos];
    }
    else {
      setNumber(0);
      return [lineupObj.firstId, lineupObj.firstPos];
    }

  }

  function changetoInt(string) {
    if (string == "one") {
      return 1;
    }
    else if (string == "two") {
      return 2;
    }
    else if (string == "three") {
      return 3;
    }
    else if (string == "four") {
      return 4;
    }
    else if (string == "five") {
      return 5;
    }
    else if (string == "six") {
      return 6;
    }
    else if (string == "seven") {
      return 7;
    }
    else if (string == "eight") {
      return 8;
    }
    else if (string == "nine") {
      return 9;
    }
    else {
      return parseInt(string);
    }

  }

  useEffect(() => {
    const firstStep = () =>
    fetch(`api/lineups/${currInning.gameId}`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedLineups) => {
        const hittersLineup = fetchedLineups.filter(lineup => currInning.hittingId == lineup.teamId);
        const pitchersLineup = fetchedLineups.filter(lineup => currInning.pitchingId == lineup.teamId);
        console.log(hittersLineup);
        console.log(pitchersLineup);
        setPitcher(pitchersLineup[0].pitcher);
        const plaposHold = grabHitter(hittersLineup[0], whatHitter);
        setHitterInfo(plaposHold);
      })

      firstStep();

    }, []);

  const addNewPlay = (newPlay) => {
    console.log("we here");
    fetch("/api/plays", {
      method: "POST",
      body: JSON.stringify(newPlay),
      headers: new Headers({
        Accept: "application/json",
        "Content-Type": "application/json"
      })
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      //Add the newly created order to local orders and update order state
      .then(receivedPlay => {
        console.log(receivedPlay);
        setLastPlay(receivedPlay.hittingResult);
      })
      .catch(err => {
        console.error(err);
      });

  }

  //Speech Packaging
  var indexSpeech = 0;
  var SpeechRecognition = window.webkitSpeechRecognition;
  var recognition = new SpeechRecognition();
  recognition.continuous = true;
  recognition.onstart = function() {
    console.log("We are listening. Try speaking into the microphone.");
  };
  recognition.onend = function() {
    recognition.abort();
  }
  recognition.onerror = function() {
    console.log("Error");
  }

  recognition.onresult = function(event) {
    var transcript = event.results[indexSpeech][0].transcript;
    var confidence = parseFloat(event.results[indexSpeech][0].confidence);
    const breakDown = transcript.split(" ");
    console.log(breakDown);
    console.log(typeof breakDown[0]);
    console.log(confidence > 0.65);
    console.log(breakDown[0] == "pitch");
    console.log(breakDown[breakDown.length - 1] == "end");
    console.log(breakDown.includes("hit"));
    if (confidence > 0.65 && breakDown[0].toLowerCase() == "pitch" && breakDown[breakDown.length - 1].toLowerCase() == "end" && breakDown.includes("hit")) {
      const pitchType = breakDown[1];
      const pitchSpeed = changetoInt(breakDown[2]);
      const pitchLocation = changetoInt(breakDown[3]);
      const hittingInfo = breakDown.slice(5);
      let loopIndex = 0;
      let hittingData = "";
      while (hittingInfo[loopIndex].toLowerCase() != "end") {
        if (loopIndex == 0) {
          hittingData = hittingData + hittingInfo[loopIndex];
        }
        else {
          hittingData = hittingData + " " + hittingInfo[loopIndex]
        }
        loopIndex = loopIndex + 1;
      }

      const myPlay = ({
        inningId: currInning.id,
        pitcherId: pitcher,
        hitterId: hitterInfo[0],
        pitchType: pitchType,
        pitchSpeed: pitchSpeed,
        pitchLocation: pitchLocation,
        hitterResult: hittingData

      });
      console.log("Made it");
      addNewPlay(myPlay);

    }
    else {
      //Fails

    }



    indexSpeech += 1;
    console.log(transcript);
    console.log(confidence);
};

  const listeningButton = (
    <Button
      style = {{ backgroundColor: buttonColor }}
      onClick={() => {
      if (buttonText == "Not Listening...") {
        recognition.start();
        setButtonText("Listening!");
        setButtonColor("#5ef266");
      }
      else {
        recognition.abort();
        setButtonText("Not Listening...");
        setButtonColor("#eb020e");
      }
    }}>{buttonText}</Button>
  );
  //End of Speech Packaging

  if (currInning == null) {
    return(<div>Loading...</div>);
  }

  console.log("Examine");
  return (
    <div>
      <Container>
        <Row>
          <Col>
            <strong>Balls:</strong>
            <small>{balls}</small>
          </Col>
          <Col>
            <strong>Strikes:</strong>
            <small>{strikes}</small>
          </Col>
          <Col>
            <strong>Outs:</strong>
            <small>{outs}</small>
          </Col>
        </Row>
        <Row>
          <Col>
            <div>Pitcher</div>
          </Col>
          <RunAndHitHold playerId={pitcher} pos={null}></RunAndHitHold>
        </Row>
        <Row>
          <Col>
            <div>Hitter</div>
          </Col>
          <RunAndHitHold playerId={hitterInfo[0]} pos={hitterInfo[1]}></RunAndHitHold>
        </Row>
        {noOneOn(frID) && <Row>
                            <Col>
                              <div>On first...</div>
                            </Col>
                            <RunAndHitHold playerId={frID} pos={null}></RunAndHitHold>
                          </Row>}
        {noOneOn(srID) && <Row>
                            <Col>
                              <div>On second...</div>
                            </Col>
                            <RunAndHitHold playerId={srID} pos={null}></RunAndHitHold>
                          </Row>}
        {noOneOn(trID) && <Row>
                            <Col>
                              <div>On third...</div>
                            </Col>
                            <RunAndHitHold playerId={trID} pos={null}></RunAndHitHold>
                          </Row>}
        <Row>
          <Col>
            {listeningButton}
          </Col>
        </Row>
        <Row>
          <Col>
            Last Play: {lastPlay}
          </Col>
        </Row>

      </Container>
    </div>
  );

}

export default InningReader;
