import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

import classes from "./StatHolders.module.css";

const PitcherStatDisplay = ({ pitcher }) => {
  const [stats, setStats] = React.useState([]);

  function gatherStats(plays, pos) {
    if (pos == 0) {
      let pitchesSeen = 0;
      let pAs = 0;
      let aBs = 0;
      let singles = 0;
      let doubles = 0;
      let triples = 0;
      let homers = 0;
      let rbis = 0;
      let runs = 0;
      let sbs = 0;
      let bbs = 0;

      plays.forEach(play => {
        pitchesSeen = pitchesSeen + 1;
        const collectionPlay = play.hitterResult.split(" ");
        const loweredPlay = collectionPlay.map(word => word.toLowerCase());
        let newRUNS = 0;
        if (loweredPlay.includes("single")) {
          const runnerResults = MoveUp(1, "", hitterInfo[0], frID, srID, trID);
          setFirstRunner(runnerResults[0]);
          setSecondRunner(runnerResults[1]);
          setThirdRunner(runnerResults[2]);
          newRUNS = runnerResults[3];
          setHits(hits + 1);
          if (runCheck(loweredPlay)) {
            setThirdRunner(0);
            setRuns(runs + 1);
          }
          setRuns(runs + newRUNS);
          setNumber(whatHitter + 1);
          setLastPlay("Single");
        }
        else if (loweredPlay.includes("double")) {
          const runnerResults = MoveUp(2, "", hitterInfo[0], frID, srID, trID);
          setFirstRunner(runnerResults[0]);
          setSecondRunner(runnerResults[1]);
          setThirdRunner(runnerResults[2]);
          newRUNS = runnerResults[3];
          setHits(hits + 1);
          if (runCheck(loweredPlay)) {
            setThirdRunner(0);
            setRuns(runs + 1);
          }
          setRuns(runs + newRUNS);
          setNumber(whatHitter + 1);
          setLastPlay("Double");
        }
        else if (loweredPlay.includes("triple")) {
          const runnerResults = MoveUp(3, "", hitterInfo[0], frID, srID, trID);
          setFirstRunner(runnerResults[0]);
          setSecondRunner(runnerResults[1]);
          setThirdRunner(runnerResults[2]);
          newRUNS = runnerResults[3];
          setHits(hits + 1);
          setRuns(runs + newRUNS);
          setNumber(whatHitter + 1);
          setLastPlay("Triple");
        }
        else if (loweredPlay.includes("homerun")) {
          const runnerResults = MoveUp(4, "", hitterInfo[0], frID, srID, trID);
          setFirstRunner(runnerResults[0]);
          setSecondRunner(runnerResults[1]);
          setThirdRunner(runnerResults[2]);
          newRUNS = runnerResults[3];
          setHits(hits + 1);
          setRuns(runs + newRUNS);
          setNumber(whatHitter + 1);
          setLastPlay("Home Run");
        }
        else if (loweredPlay.includes("fly") || loweredPlay.includes("flyout")) {
          const runnerResults = MoveUp(0, "F", hitterInfo[0], frID, srID, trID);
          setFirstRunner(runnerResults[0]);
          setSecondRunner(runnerResults[1]);
          setThirdRunner(runnerResults[2]);
          newRUNS = runnerResults[3];
          if (runCheck(loweredPlay)) {
            setThirdRunner(0);
            setRuns(runs + 1);
          }
          setOuts(outs + 1);
          setBalls(0);
          setStrikes(0);
          setNumber(whatHitter + 1);
          setLastPlay("Fly Out");
        }
        else if (loweredPlay.includes("ground") || loweredPlay.includes("groundout")) {
          const runnerResults = MoveUp(0, "G", hitterInfo[0], frID, srID, trID);
          setFirstRunner(runnerResults[0]);
          setSecondRunner(runnerResults[1]);
          setThirdRunner(runnerResults[2]);
          newRUNS = runnerResults[3];
          if (runCheck(loweredPlay)) {
            setThirdRunner(0);
            setRuns(runs + 1);
          }
          setOuts(outs + 1);
          setBalls(0);
          setStrikes(0);
          setNumber(whatHitter + 1);
          setLastPlay("Ground Out");
        }
        else if (loweredPlay.includes("strike")) {
          if (strikes == 2) {
            setOuts(outs + 1);
            setBalls(0);
            setStrikes(0);
            setLastPlay("Strikeout");
          }
          else {
            setStrikes(strikes + 1);
          }
        }
        else if (loweredPlay.includes("ball")) {
          if (balls == 3) {
            setBalls(0);
            setStrikes(0);
            setFirstRunner(hitterInfo[0]);
            setLastPlay("Walk");
          }
          else {
            setBalls(balls + 1);
          }
        }
        else if (loweredPlay.includes("foul")) {
          if (strikes == 2) {
            setLastPlay("Foul Ball");
          }
          else {
            setStrikes(strikes + 1);
            setLastPlay("Foul Ball");
          }

        }
        else {
          setLastPlay("Please try again.")
        }
      });

      return ({
        pitchesSeen: pitchesSeen,
        pAs: pAs,
        aBs: aBs,
        singles: singles,
        doubles: doubles,
        triples: triples,
        homers: homers,
        rbis: rbis,
        runs: runs,
        sbs: sbs,
        bbs: bbs
      });
    }
    else {
      let pitchesThrown = 0;
      let fbs = 0;
      let chs = 0;
      let cvs = 0;
      let other = 0;
      let outsRecord = 0;
      let hA = 0;
      let rA = 0;
      let ks = 0;
      let bbs = 0;

      plays.forEach(play => {
        pitchesThrown = pitchesThrown + 1;
        //Pitch Type
        if (play.pitchType == "fastball") {
          fbs = fbs + 1;
        }
        else if (play.pitchType == "changeup") {
          chs = chs + 1;
        }
        else if (play.pitchType == "curveball") {
          cvs = cvs + 1;
        }
        else {
          other = other + 1;
        }
        //Result
        const collectionPlay = play.hitterResult.split(" ");
        if (collectionPlay.includes("single") || collectionPlay.includes("double") || collectionPlay.includes("triple") || collectionPlay.includes("homerun")) {
          hA = hA + 1;
          if (collectionPlay.includes("run") || collectionPlay.includes("runs")) {
            if (collectionPlay.includes("one")) {
              rA = rA + 1;
            }
            else if (collectionPlay.includes("two")) {
              rA = rA + 2;
            }
            else if (collectionPlay.includes("three")) {
              rA = rA + 3;
            }
            else {
              rA = rA + 4;
            }
          }
        }
        else if (collectionPlay.includes("strikeout")) {
          ks = ks + 1;
          outsRecord = outsRecord + 1;
        }
        else if (collectionPlay.includes("walk")) {
          bbs = bbs + 1;
        }
        else if (!collectionPlay.includes("ball") || !collectionPlay.includes("strike")) {
          outsRecord = outsRecord + 1;
        }
      });

      return ({
        pitchesThrown: pitchesThrown,
        fbs: fbs,
        chs: chs,
        cvs: cvs,
        other: other,
        hA: hA,
        rA: rA,
        ks: ks,
        bbs: bbs
      });
    }
  }

  useEffect(() => {
    fetch(`api/plays`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedPlays) => {
        const filteredPlays = fetchedPlays.filter(play => play.pitcherId == pitcher.id);
        const myStats = gatherStats(filteredPlays, pitcher.position);
        setStats(myStats);
      }).catch(err => {
        console.error(err);
      });
    }, []);

    if (pitcher == null) {
      return (<div>Loading...</div>);
    }
    else if (pitcher.outsRecord == 0) {
      console.log("This situation");
      return (
        <div>
          <Row>
            <Col>
              <strong>{pitcher.name}</strong>
              <strong>No stats recorded.</strong>
              <strong>{pitcher.outsRecord}</strong>
            </Col>
          </Row>
        </div>
      );

    }
    else {
      console.log("THAT situation");
      return (
        <div>
          <strong className={classes.StatHolder}>{pitcher.name}</strong>
          <strong className={classes.StatHolder}>{parseFloat(stats.fbs/stats.pitchesThrown).toFixed(2)}</strong>
          <strong className={classes.StatHolder}>{parseFloat(stats.outsRecord/3).toFixed(2)}</strong>
          <strong className={classes.StatHolder}>{parseFloat((stats.rA*9)/(stats.outsRecord/3)).toFixed(2)}</strong>
          <strong className={classes.StatHolder}>{parseFloat((stats.hA+stats.bbs)/(stats.outsRecord/3)).toFixed(2)}</strong>
          <strong className={classes.StatHolder}>{parseFloat(stats.ks/(stats.outsRecord/3) * 9).toFixed(2)}</strong>
          <strong className={classes.StatHolder}>{parseFloat(stats.ks/stats.bbs).toFixed(2)}</strong>
        </div>
      );
    }


}

export default PitcherStatDisplay;
