//styling and react components
import React, { useState, useEffect } from "react";

const LineupPlayer = ({ playerId }) => {
  const [season, setSeason] = React.useState(null);
  const [actualPlayer, setActualPlayer] = React.useState(null);

  function gatherStats(plays, pos) {
    if (pos === 0) {
      let pitchesSeen = 0;
      let pAs = 0;
      let aBs = 0;
      let singles = 0;
      let doubles = 0;
      let triples = 0;
      let homers = 0;
      let rbis = 0;
      let runs = 0;
      let sbs = 0;
      let bbs = 0;

      plays.forEach(play => {
        pitchesSeen = pitchesSeen + 1;
        const collectionPlay = play.hitterResult.split(" ");
        if (!collectionPlay.includes("ball") && !collectionPlay.includes("strike")) {
          pAs = pAs + 1;
          if (!collectionPlay.includes("walk")) {
            aBs = aBs + 1;
            if (collectionPlay.includes("single")) {
              singles = singles + 1;
            }
            else if (collectionPlay.includes("double")) {
              doubles = doubles + 1;
            }
            else if (collectionPlay.includes("triple")) {
              triples = triples + 1;
            }
            else if (collectionPlay.includes("homerun")) {
              homers = homers + 1;
            }
          }
          else {
            bbs = bbs + 1;
          }
          if (collectionPlay.includes("run") || collectionPlay.includes("runs")) {
            if (collectionPlay.includes("one")) {
              rbis = rbis + 1;
            }
            else if (collectionPlay.includes("two")) {
              rbis = rbis + 2;
            }
            else if (collectionPlay.includes("three")) {
              rbis = rbis + 3;
            }
            else {
              rbis = rbis + 4;
            }
          }
        }
      });

      return ({
        pitchesSeen: pitchesSeen,
        pAs: pAs,
        aBs: aBs,
        singles: singles,
        doubles: doubles,
        triples: triples,
        homers: homers,
        rbis: rbis,
        runs: runs,
        sbs: sbs,
        bbs: bbs
      });
    }
    else {
      let pitchesThrown = 0;
      let fbs = 0;
      let chs = 0;
      let cvs = 0;
      let other = 0;
      let outsRecord = 0;
      let hA = 0;
      let rA = 0;
      let ks = 0;
      let bbs = 0;

      plays.forEach(play => {
        pitchesThrown = pitchesThrown + 1;
        //Pitch Type
        if (play.pitchType === "fastball") {
          fbs = fbs + 1;
        }
        else if (play.pitchType === "changeup") {
          chs = chs + 1;
        }
        else if (play.pitchType === "curveball") {
          cvs = cvs + 1;
        }
        else {
          other = other + 1;
        }
        //Result
        const collectionPlay = play.hitterResult.split(" ");
        if (collectionPlay.includes("single") || collectionPlay.includes("double") || collectionPlay.includes("triple") || collectionPlay.includes("homerun")) {
          hA = hA + 1;
          if (collectionPlay.includes("run") || collectionPlay.includes("runs")) {
            if (collectionPlay.includes("one")) {
              rA = rA + 1;
            }
            else if (collectionPlay.includes("two")) {
              rA = rA + 2;
            }
            else if (collectionPlay.includes("three")) {
              rA = rA + 3;
            }
            else {
              rA = rA + 4;
            }
          }
        }
        else if (collectionPlay.includes("strikeout")) {
          ks = ks + 1;
          outsRecord = outsRecord + 1;
        }
        else if (collectionPlay.includes("walk")) {
          bbs = bbs + 1;
        }
        else if (!collectionPlay.includes("ball") || !collectionPlay.includes("strike")) {
          outsRecord = outsRecord + 1;
        }
      });

      return ({
        pitchesThrown: pitchesThrown,
        fbs: fbs,
        chs: chs,
        cvs: cvs,
        other: other,
        hA: hA,
        rA: rA,
        ks: ks,
        bbs: bbs
      });
    }
  }

  useEffect(() => {
      const theGuy = fetch(`api/players`)
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        })
        .then((fetchedPlayers) => {
          const myGuy = fetchedPlayers.filter(player => player.id === playerId);
          setActualPlayer(myGuy[0]);
          return myGuy[0];
        }).catch(err => {
          console.error(err);
        });

      theGuy.then((person) => {
        fetch(`api/plays`)
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedPlays) => {
            if (person.position !== 1) {
              const myTotPlays = fetchedPlays.filter(play => person.id === play.hitterId);
              setSeason(gatherStats(myTotPlays, 0));
            }
            else {
              const myTotPlays = fetchedPlays.filter(play => person.id === play.pitcherId);
              setSeason(gatherStats(myTotPlays, 1));
            }
          }).catch(err => {
            console.error(err);
          });


      })
    }, [playerId]);

  if (actualPlayer !== null && season !== null) {
    if (actualPlayer.position !== 1) {
      return (
        <strong>{actualPlayer.name}</strong>

      );
    }
    else {
      return (
        <strong>{actualPlayer.name}</strong>

      );
    }
  }

  return (<div>Loading...</div>);




}

export default LineupPlayer;
