import React, { useState, useEffect } from "react";
import "./App.css";

import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

import TeamAccessHolder from "./components/TeamAccessHolder"
import LoginScreen from "./components/LoginScreen"


const Header = styled.h1`
  background-color: blue;
  text-align: center;
  padding: 1rem;
  color: white;
`;
const ButtonDisplay = styled.div`
  background-color: red;
  padding: 0.5rem 0;
  margin-bottom: 0.5rem;
`;

const Message = styled.div`
  background-color: white;
  padding: 70px 0;
  text-align: center;
  color: black;
  /*background-image: url('Jefferson.png')*/
`;

const App = () => {
  const [currUser, setUser] = React.useState(null);
  const [access, setAccess] = React.useState(null);
  const [personNew, setStat] = React.useState(0);
  const [signinError, setError] = React.useState("");

  const [mode, setMode] = React.useState("Home");

  console.log(currUser);

  useEffect(() => {
      const fetchUser = () => {
        fetch("/api/users")
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedUsers) => {
            // Create a copy of the collections, replacing the current article if this is an edit
            const checkedSignedIn = fetchedUsers.filter(user => user.signedIn == 1);
            if (checkedSignedIn.length != 0) {
              setUser(checkedSignedIn[0]);
              const myID = checkedSignedIn[0].id;
              return myID
            }
          }).then((thatID) => {


          })
          .catch((err) => console.error(err)); // eslint-disable-line no-console
      };

      const fetchAccess = () => {
        fetch("/api/teamaccess")
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedAccesses) => {
            // Create a copy of the collections, replacing the current article if this is an edit
            if (currUser == null) {
              setAccess([]);
            }
            else {
              const checkedAcc = fetchedAccesses.filter(access => access.userId == currUser.id);
              setAccess(checkedAcc)
            }

          })
          .catch((err) => console.error(err)); // eslint-disable-line no-console
      }

      fetchUser();

    }, []);

  const handleSignInReturn = (username, password) => {
    fetch("/api/users")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedUsers) => {
        // Create a copy of the collections, replacing the current article if this is an edit
        const checkedSignedIn = fetchedUsers.filter(user => user.username == username && user.password == password);
        if (checkedSignedIn.length != 0) {
          setUser(checkedSignedIn[0]);
          setStat(0);
        }
        else {
          setStat(1)
        }
      })
      .catch((err) => console.error(err)); // eslint-disable-line no-console
  };
  const handleSignOutReturn = (username) => {

  }

  const handleAccountCreation = (personCredentials) =>
    {
      fetch("/api/users")
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        })
        .then((fetchedUsers) => {
          // Create a copy of the collections, replacing the current article if this is an edit
          const checkedSignedIn = fetchedUsers.filter(user => user.username == personCredentials.username);
          if (checkedSignedIn.length != 0) {
            setStat(2);
          }
          else {
            fetch("/api/users", {
              method: "POST",
              body: JSON.stringify(personCredentials),
              headers: new Headers({
                Accept: "application/json",
                "Content-Type": "application/json"
              })
            })
              .then(response => {
                if (response.ok) {
                  return response.json();
                }
                throw new Error(response.statusText);
              })
              //Add the newly created order to local orders and update order state
              .then(receivedPerson => {
                setUser(receivedPerson);
              })
              .catch(err => {
                console.error(err);
              });
          }
        })
        .catch((err) => console.error(err)); // eslint-disable-line no-console
    };

  const teamList = (<div></div>);

  if (access != null) {
    teamList = access.map(access => <TeamAccessHolder teamaccess={access}></TeamAccessHolder>)
  }


  if (currUser == null) {
    return (
      <div>
        <Header>Voice-Activated Baseball Scoring</Header>
        <Container>
          <LoginScreen signIn={handleSignInReturn} createAccount={handleAccountCreation}></LoginScreen>
        </Container>
      </div>
    );
  }


  return (

    <div>
      <Header>Voice-Activated Baseball Scoring</Header>
      <Message>Thanks for signing-in! Please select one of your teams below to get started</Message>
      <Container>
        <ul>{teamList}</ul>
      </Container>
    </div>
  );

};

export default App;
