import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const TeamAccessHolder = ({ teamaccess }) => {
  const [team, setTeam] = React.useState(null);

  useEffect(() => {
      const fetchTeam = () => {
        fetch("/api/teams")
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedTeams) => {
            // Create a copy of the collections, replacing the current article if this is an edit
            console.log(teamaccess.teamId);
            const checkedTeam = fetchedTeams.filter(team => team.id == teamaccess.teamId);
            if (checkedTeam.length != 0) {
              setTeam(checkedTeam[0]);
            }
          })
          .catch((err) => console.error(err)); // eslint-disable-line no-console
      };

      fetchTeam();

    }, []);


  if (team != null) {
    return (
      <div>
        <Row>
          <strong>Team Mascot: {team.name} ({teamaccess.type})</strong>
        </Row>
        <Row>
          <strong>Team Location: {team.location}</strong>
        </Row>
        <Row>
          <Col>
            <small>Coach: {team.coach}</small>
          </Col>
          <Col>
            <small>Division: {team.division}</small>
          </Col>
        </Row>
      </div>
    );
  }
  return (
    <div>Loading...</div>
  );

}

export default TeamAccessHolder;
