import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const HitterStatDisplay = ({ hitter }) => {

  const [stats, setStats] = React.useState([]);

  function gatherStats(plays, pos) {
    if (pos == 0) {
      let pitchesSeen = 0;
      let pAs = 0;
      let aBs = 0;
      let singles = 0;
      let doubles = 0;
      let triples = 0;
      let homers = 0;
      let rbis = 0;
      let runs = 0;
      let sbs = 0;
      let bbs = 0;

      plays.forEach(play => {
        pitchesSeen = pitchesSeen + 1;
        const collectionPlay = play.hitterResult.split(" ");
        if (!collectionPlay.includes("ball") && !collectionPlay.includes("strike")) {
          pAs = pAs + 1;
          if (!collectionPlay.includes("walk")) {
            aBs = aBs + 1;
            if (collectionPlay.includes("single")) {
              singles = singles + 1;
            }
            else if (collectionPlay.includes("double")) {
              doubles = doubles + 1;
            }
            else if (collectionPlay.includes("triple")) {
              triples = triples + 1;
            }
            else if (collectionPlay.includes("homerun")) {
              homers = homers + 1;
            }
          }
          else {
            bbs = bbs + 1;
          }
          if (collectionPlay.includes("run") || collectionPlay.includes("runs")) {
            if (collectionPlay.includes("one")) {
              rbis = rbis + 1;
            }
            else if (collectionPlay.includes("two")) {
              rbis = rbis + 2;
            }
            else if (collectionPlay.includes("three")) {
              rbis = rbis + 3;
            }
            else {
              rbis = rbis + 4;
            }
          }
        }
      });

      return ({
        pitchesSeen: pitchesSeen,
        pAs: pAs,
        aBs: aBs,
        singles: singles,
        doubles: doubles,
        triples: triples,
        homers: homers,
        rbis: rbis,
        runs: runs,
        sbs: sbs,
        bbs: bbs
      });
    }
    else {
      let pitchesThrown = 0;
      let fbs = 0;
      let chs = 0;
      let cvs = 0;
      let other = 0;
      let outsRecord = 0;
      let hA = 0;
      let rA = 0;
      let ks = 0;
      let bbs = 0;

      plays.forEach(play => {
        pitchesThrown = pitchesThrown + 1;
        //Pitch Type
        if (play.pitchType == "fastball") {
          fbs = fbs + 1;
        }
        else if (play.pitchType == "changeup") {
          chs = chs + 1;
        }
        else if (play.pitchType == "curveball") {
          cvs = cvs + 1;
        }
        else {
          other = other + 1;
        }
        //Result
        const collectionPlay = play.hitterResult.split(" ");
        if (collectionPlay.includes("single") || collectionPlay.includes("double") || collectionPlay.includes("triple") || collectionPlay.includes("homerun")) {
          hA = hA + 1;
          if (collectionPlay.includes("run") || collectionPlay.includes("runs")) {
            if (collectionPlay.includes("one")) {
              rA = rA + 1;
            }
            else if (collectionPlay.includes("two")) {
              rA = rA + 2;
            }
            else if (collectionPlay.includes("three")) {
              rA = rA + 3;
            }
            else {
              rA = rA + 4;
            }
          }
        }
        else if (collectionPlay.includes("strikeout")) {
          ks = ks + 1;
          outsRecord = outsRecord + 1;
        }
        else if (collectionPlay.includes("walk")) {
          bbs = bbs + 1;
        }
        else if (!collectionPlay.includes("ball") || !collectionPlay.includes("strike")) {
          outsRecord = outsRecord + 1;
        }
      });

      return ({
        pitchesThrown: pitchesThrown,
        fbs: fbs,
        chs: chs,
        cvs: cvs,
        other: other,
        hA: hA,
        rA: rA,
        ks: ks,
        bbs: bbs
      });
    }
  }

  useEffect(() => {
    fetch(`api/plays`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedPlays) => {
        const filteredPlays = fetchedPlays.filter(play => play.hitterId == hitter.id);
        const myStats = gatherStats(filteredPlays, hitter.position);
        setStats(myStats);
      }).catch(err => {
        console.error(err);
      });
    }, []);

    if (hitter == null) {
      return (<div>Loading...</div>);
    }
    else {
      return (
        <div>
          <Row>
            <Col>
              <strong>{hitter.name}</strong>
            </Col>
            <Col>
              <div value={(stats.singles+stats.doubles+stats.triples+stats.homers)/stats.aBs}></div>
            </Col>
            <Col>
              <div value={(stats.singles+stats.doubles+stats.triples+stats.homers+stats.bbs)/stats.pAs}></div>
            </Col>
            <Col>
              <div value={(stats.singles + (stats.doubles*2) + (stats.triples*3) + (stats.homers*4))/stats.aBs}></div>
            </Col>
            <Col>
              <div value={stats.rbis}></div>
            </Col>
          </Row>
        </div>
      );
    }


}

export default HitterStatDisplay;
