import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const RosterSelection = ({ team }) => {
  const [hitters, setHitters] = React.useState([]);
  const [pitchers, setPitchers] = React.useState([]);

  useEffect(() => {
      const fetchRoster = () => {
        fetch(`api/players/${team.id}`)
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        })
        .then((fetchedPlayers) => {
          const gottenHitters = fetchedPlayers.filter(player => player.position != 1);
          const gottenPitchers = fetchedPlayers.filter(player => player.position == 1);
          setHitters(gottenHitters);
          setPitchers(gottenPitchers);
        })
      }

      fetchRoster();

    }, []);




};

const EventHolder = ({ team }) => {
  const [eventMode, setEventMode] = React.useState("Home");
  const [escucha, setEscucha] = React.useState(false);
  //Pitching detail
  const [pitchOn, setPitchOn] = React.useState()

  //Speech Packaging
  var indexSpeech = 0;
  var SpeechRecognition = window.webkitSpeechRecognition;
  var recognition = new SpeechRecognition();
  recognition.continuous = false;
  recognition.onstart = function() {
    console.log("We are listening. Try speaking into the microphone.");
  };
  recognition.onend = function() {
    recognition.abort();
  }
  recognition.onerror = function() {
    console.log("Error");
  }

  recognition.onresult = function(event) {
    var transcript = event.results[indexSpeech][0].transcript;
    var confidence = event.results[indexSpeech][0].confidence;
    indexSpeech += 1;
    console.log(transcript);
    console.log(confidence);
};

  const listeningButton = (
    <Button onClick={() => {
      if (escucha == false) {
        recognition.start();
        setEscucha(true);
      }
      else {
        recognition.abort();
        console.log("Speech has stopped");
        setEscucha(false);
      }
      }}>Test</Button>
  );

  //Other Objects for display


  if (eventMode == "Home") {
    return (<div>
              <h1>WELCOME TO THE GAME CREATOR.</h1>
              <strong>Please examine the options below before continuing</strong>
            </div>);
  }
  else {
    return (
      <div>
        {listeningButton}
      </div>
    );

  }






}

export default EventHolder;
