import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const LoginScreen = ({ signIn, createAccount }) => {

  const [name, setName] = React.useState("");
  const [pword, setPWord] = React.useState("");

  const Title = styled.h2`
    background-color: white;
    text-align: center;
    padding: 1rem;
    color: black;
  `;

  const Message = styled.div`
    background-color: white;
    padding: 70px 0;
    text-align: center;
    color: black;
    /*background-image: url('Jefferson.png')*/
  `;

  const ButtonHolder = styled.div`
    background-color: black;
    padding: 70px 0;
    text-align: center;
    /*background-image: url('Jefferson.png')*/
  `;

  const signInButton = (
    <Button
      style={{ backgroundColor: "#0000ff", display: "flex", justifyContent: "center"}}
      onClick={() => {
        signIn(name, pword);
      }}
      >Sign In!</Button>
  );

  const createAccountButton = (
    <Button
      style={{ backgroundColor: "#0000ff", display: "flex", justifyContent: "center"}}
      onClick={() => {
        createAccount(constructNewPerson());
      }}
      >Create Account!</Button>
  );

  const constructNewPerson = () => ({
    username: name,
    password: pword,
    signedIn: 1
  });

  return (
    <div>
      <Title>Welcome to VABS!</Title>
      <Message>...voice-activated baseball scorer, that is. Please sign-in with a defined account in order to use the service</Message>
      <Message>If you do not have an account, use the create account button below after filling out the two-fields!</Message>
      <Row>
        <Col>
          <label>Username: </label>
        </Col>
        <Col>
          <input value={name} onChange={event => setName(event.target.value)}></input>
        </Col>
      </Row>
      <Row>
        <Col>
          <label>Password: </label>
        </Col>
        <Col>
          <input value={pword} onChange={event => setPWord(event.target.value)}></input>
        </Col>
      </Row>
      <ButtonHolder>
        <Row>
          <Col>
            {signInButton}
          </Col>
          <Col>
            {createAccountButton}
          </Col>
        </Row>
      </ButtonHolder>
    </div>
  );

}

export default LoginScreen;
