import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

import LineupPlayer from "./LineupPlayer";
import InningReader from "./InningReader";

const LineupHolder = ({ game, teamId }) => {
  const [lineup, setLineup] = React.useState(null);
  const [bench, setBench] = React.useState(null);
  const [bullpen, setBullpen] = React.useState(null);

  function checkIn(playerId, lineup) {
    if (lineup.firstPlayer == playerId || lineup.secondPlayer == playerId || lineup.thirdPlayer == playerId || lineup.fourthPlayer == playerId || lineup.fifthPlayer == playerId || lineup.sixthPlayer == playerId || lineup.seventhPlayer == playerId || lineup.eighthPlayer == playerId || lineup.ninthPlayer == playerId || lineup.pitcher == playerId) {
      return true;
    }
    return false;
  }

  useEffect(() => {
      const theLineup = fetch(`api/players/${teamId}`)
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        })
        .then((fetchedPlayers) => {
          return fetchedPlayers;
        }).catch(err => {
          console.error(err);
        });

        theLineup.then(data => {
          fetch(`api/lineups/${game.id}`)
            .then((response) => {
              if (response.ok) {
                return response.json();
              }
              throw new Error(response.statusText);
            })
            .then((fetchedLineups) => {
              const trueLineList = fetchedLineups.filter(lineup => teamId == lineup.teamId);
              const trueLine = trueLineList[0];
              const myBullpen = [];
              const myBench = [];
              data.forEach(player => {
                if (player.position != 1) {
                  if (!checkIn(player.id, trueLine)) {
                    myBench.push(player);
                  }
                }
                else {
                  if (!checkIn(player.id, trueLine)) {
                    myBullpen.push(player);
                  }
                }
              });

              setLineup(trueLine);
              setBench(myBench);
              setBullpen(myBullpen);
            }).catch(err => {
              console.error(err);
            });
        })
    }, []);

    const lineupMap = lineup.map(player => <Row>
                                              <LineupPlayer player={player}></LineupPlayer>
                                            </Row>);
    const bullpenMap = bullpen.map(pitcher => <Row>
                                                <small>{pitcher.name}</small>
                                              </Row>);
    const benchMap = bench.map(hitter => <Row>
                                          <small>{hitter.name}</small>
                                        </Row>);

    return (
      <div>
        <Container>
          <Row>
            <strong>{teamId}'s Lineup</strong>
          </Row>
          {lineupMap}
        </Container>
        <Container>
          <Row>
            <Col>
              <small>Bullpen</small>
              {bullpenMap}
            </Col>
            <Col>
              <small>Bench</small>
              {benchMap}
            </Col>
          </Row>
        </Container>
      </div>
    );
}

const GameTitleAndBox = ({ game }) => {
  const [homeTeam, setHome] = React.useState(null);
  const [roadTeam, setRoad] = React.useState(null);
  const [innings, setInnings] = React.useState([]);

  useEffect(() => {
      const setTeams = () => {
        fetch(`api/teams`)
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedTeams) => {
            const filteredTeams = fetchedTeams.filter(team => team.id == game.awayId || team.id == game.homeId);
            if (filteredTeams[0].id == game.homeId) {
              setHome(filteredTeams[0]);
              setRoad(filteredTeams[1]);
            }
            else {
              setHome(filteredTeams[1]);
              setRoad(filteredTeams[0]);
            }
          }).catch(err => {
            console.error(err);
          });
      }
      const getdaInnings = () => {
        fetch(`api/innings/${game.id}`)
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedInnings) => {
            setInnings(fetchedInnings);
          }).catch(err => {
            console.error(err);
          });
      }
      setTeams();
      getdaInnings();
    }, []);

    const awayOpps = [];
    const homeOpps = [];
    let homeHits = 0;
    let awayHits = 0;
    let homeRuns = 0;
    let awayRuns = 0;
    innings.forEach((inning, index) => {
      if (index % 2 == 0) {
        awayOpps.push(inning.runs);
        awayHits = awayHits + inning.hits;
        awayRuns = awayRuns + inning.runs;
      }
      else {
        homeOpps.push(inning.runs);
        homeHits = homeHits + inning.hits;
        homeRuns = homeRuns + inning.runs;
      }
    });

    const awayMapping = awayOpps.map(opp => <Col>
                                              <small>{opp}</small>
                                            </Col>);
    const homeMapping = homeOpps.map(opp => <Col>
                                              <small>{opp}</small>
                                            </Col>);

    if (homeTeam == null || roadTeam == null) {
      return (<div>Loading...</div>);
    }

    return (
      <div>
        <Row>
          <h1>{roadTeam.name} at {homeTeam.name}</h1>
        </Row>
        <Row>
          <Col>
            <small>Team</small>
          </Col>
          <Col>
            <small>1</small>
          </Col>
          <Col>
            <small>2</small>
          </Col>
          <Col>
            <small>3</small>
          </Col>
          <Col>
            <small>4</small>
          </Col>
          <Col>
            <small>5</small>
          </Col>
          <Col>
            <small>6</small>
          </Col>
          <Col>
            <small>7</small>
          </Col>
          <Col>
            <small>8</small>
          </Col>
          <Col>
            <small>9</small>
          </Col>
          <Col>
            <small>R</small>
          </Col>
          <Col>
            <small>H</small>
          </Col>
        </Row>
        <Row>
          <Col>
            <small>{roadTeam.name}</small>
          </Col>
          {awayMapping}
          <Col>
            <small>{awayRuns}</small>
          </Col>
          <Col>
            <small>{awayHits}</small>
          </Col>
        </Row>
        <Row>
          <Col>
            <small>{homeTeam.name}</small>
          </Col>
          {homeMapping}
          <Col>
            <small>{homeRuns}</small>
          </Col>
          <Col>
            <small>{homeHits}</small>
          </Col>
        </Row>
      </div>
    );


}

const GameDisplay = ({ gameId, back }) => {
  const [myGame, setGame] = React.useState(null);
  const [currInn, setCurrInn] = React.useState(null);
  const [whoHitting, setHittingId] = React.useState("Away");
  const [awayOrder, setAwayOrder] = React.useState(0);
  const [homeOrder, setHomeOrder] = React.useState(0);


  useEffect(() => {
      const setUP = fetch("api/games")
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        })
        .then((fetchedGames) => {
          console.log(gameId);
          const filteredGames = fetchedGames.filter(game => game.id == gameId);
          console.log(filteredGames);
          setGame(filteredGames[0]);
          return filteredGames[0].id
        }).catch(err => {
          console.error(err);
        }).catch(err => {
          console.error(err);
        });

        setUP.then((data) => {
          fetch(`api/innings/${data}`)
            .then((response) => {
              if (response.ok) {
                return response.json();
              }
              throw new Error(response.statusText);
            })
            .then((fetchedInnings) => {
              if (fetchedInnings.length == 0) {
                const myInning = ({
                  gameId: data,
                  number: 1,
                  runs: 0,
                  balls: 0,
                  strikes: 0,
                  outs: 0
                });
                fetch("/api/innings", {
                  method: "POST",
                  body: JSON.stringify(myInning),
                  headers: new Headers({
                    Accept: "application/json",
                    "Content-Type": "application/json"
                  })
                })
                  .then(response => {
                    if (response.ok) {
                      return response.json();
                    }
                    throw new Error(response.statusText);
                  })
                  .then((returnedInning) => {
                    setCurrInn(returnedInning);
                    setHittingId("Away");
                  })
              }
              else {
                if (fetchedInnings.length % 2 == 0) {
                  setHittingId("Home");
                }
                else {
                  setHittingId("Away");
                }
                setCurrInn(fetchedInnings[fetchedInnings.length - 1]);
              }
            })

        })

    }, []);
  if (myGame == null) {
    return (
      <div>Loading...</div>
    );
  }

  if (whoHitting == "Away") {
    return (
      <div>
        <Container>
          <GameTitleAndBox game={myGame}></GameTitleAndBox>
          <Row>
            <Col>
              <LineupHolder game={myGame} teamId={myGame.awayId}></LineupHolder>
            </Col>
            <Col>
              <InningReader currInning={currInn} hitOrder={awayOrder}></InningReader>
            </Col>
            <Col>
              <LineupHolder game={myGame} teamId={myGame.homeId}></LineupHolder>
            </Col>
          </Row>
          <Row>
            <div>This will hold instructions</div>
          </Row>
        </Container>
      </div>
    );
  }

  return (
    <div>
      <Container>
        <GameTitleAndBox game={myGame}></GameTitleAndBox>
        <Row>
          <Col>
            <LineupHolder game={myGame} teamId={myGame.awayId}></LineupHolder>
          </Col>
          <Col>
            <InningReader currInning={currInn} hitOrder={homeOrder}></InningReader>
          </Col>
          <Col>
            <LineupHolder game={myGame} teamId={myGame.homeId}></LineupHolder>
          </Col>
        </Row>
        <Row>
          <div>This will hold instructions</div>
        </Row>
      </Container>
    </div>
  );



}

export default GameDisplay;
