import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

import PlayerHolder from "./PlayerHolder";
import FanHolder from "./FanHolder";
import PlayerAdder from "./PlayerAdder";
import FanAdder from "./FanAdder";

const AddDeletePerson = ({ team, type, back, returnHome }) => {
  const [list, setList] = React.useState([]);

  useEffect(() => {
    const buildList = () => {
      if (type == "Players") {
        fetch("/api/players")
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedPlayers) => {
            const actualList = fetchedPlayers.filter(player => player.teamId == team.id);
            setList(actualList);
          }).catch((err) => console.error(err)); // eslint-disable-line no-console
      }
      else if (type == "Scorers") {
        const allAccess = fetch(`/api/teamaccess`)
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          }).then((info) => {
              const returnedInfo = info.filter(access => access.teamId == team.id && access.type == 2);
              return returnedInfo;

            }
          ).catch(error => {
            console.log(error);
          });

          allAccess.then(data => {
            const users = fetch(`/api/users`)
              .then(response => {
                if (!response.ok) {
                  throw new Error(response.status_text);
                }
                return response.json();
              })
              .then(data => {
                const trueList = data.filter(user => allAccess.includes(user.id));
                setList(trueList);
              })
              .catch(error => {
                console.log(error);
              });


          })
      }

      else if (type == "Fans") {
        const allAccess = fetch(`/api/teamaccess`)
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          }).then((info) => {
              const returnedInfo = info.filter(access => access.teamId == team.id);
              return returnedInfo;
            }
          ).catch(error => {
            console.log(error);
          });

          allAccess.then(data => {
            const users = fetch(`/api/users`)
              .then(response => {
                if (!response.ok) {
                  throw new Error(response.status_text);
                }
                return response.json();
              })
              .then(data => {
                const trueList = data.filter(user => allAccess.includes(user.id));
                setList(trueList);
              })
              .catch(error => {
                console.log(error);
              });


          })
      }
    }
    buildList();
    }, []);

    //const handleDeletePerson = (person) => {


    //};

    const handleAddPlayer = (player) => {
        fetch("/api/players", {
          method: "POST",
          body: JSON.stringify(player),
          headers: new Headers({
            Accept: "application/json",
            "Content-Type": "application/json"
          })
        })
          .then(response => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          //Add the newly created order to local orders and update order state
          .then(receivedPerson => {
            console.log("NEW LIST CREATION");
            const newList = [...foodList, receivedPerson];
            console.log(newList);
            setList(newList);
          })
          .catch(err => {
            console.error(err);
          });
    }

    const handleAddUser = (accessSelected) => {
      fetch("/api/teamaccess", {
        method: "POST",
        body: JSON.stringify(accessSelected),
        headers: new Headers({
          Accept: "application/json",
          "Content-Type": "application/json"
        })
      })
        .then(response => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        })
        .catch(err => {
          console.error(err);
        });
    }

    const backButton = (
      <Button onClick={() => back()}>Back</Button>
    );

    console.log(list);

    if (list != null && type == "Players") {
      const trueMapping = list.map(person => <PlayerHolder player={person} teamLoc={team.location} teamName={team.name}></PlayerHolder>)
      return (
        <div>
          <Row>
            <Col>
              <ul> {trueMapping} </ul>
            </Col>
            <Col>
              <PlayerAdder teamId={team.id} addFunc={handleAddPlayer}></PlayerAdder>
            </Col>
          </Row>
          <Row>
            {backButton}
          </Row>
        </div>
      );

    }
    else if (list != null) {
      const trueMapping = list.map(person => <FanHolder person={person}></FanHolder>)
      return (
        <div>
          <Row>
            <Col>
              <ul> {trueMapping} </ul>
            </Col>
            <Col>
              <FanAdder teamId={team.id} addFunc={handleAddUser}></FanAdder>
            </Col>
          </Row>
          <Row>
            {backButton}
          </Row>
        </div>
      );
    }
    else {
      return (<div>Loading...</div>);
    }

}

export default AddDeletePerson;
