import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const FanAdder = ({ teamId, addFunc }) => {
  const [users, setUsers] = React.useState([]);
  const [selectedId, setSelectedId] = React.useState(-1);
  const [type, setType] = React.useState(0);
  const [date, setDate] = React.useState("");

  useEffect(() => {
      const fetchUsers = () => {
        fetch("/api/users")
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedUsers) => {
            // Create a copy of the collections, replacing the current article if this is an edit
            setUsers(fetchedUsers);
          }).catch((err) => console.error(err)); // eslint-disable-line no-console
      };
      fetchUsers();

    }, []);

  const constructNewAccess = () => ({
    userId: selectedId,
    teamId: teamId,
    type: type,
    endDate: date
  });

  const userMapping = users.map(user => <option key={user.id} value={user.id}>{user.username}</option>);

  return (
    <div>
      <Container>
        <Row>
          <Col>
            <label>Select from our User Database! (If the person you see is not here, have them make an account):</label>
          </Col>
          <Col>
            <select value={selectedId} onChange={(event) => setSelectedId(parseInt(event.target.value,10))}>
            {userMapping}
            </select>
          </Col>
        </Row>
        <Row>
          <Col>
            <label>Access Level:</label>
          </Col>
          <Col>
            <select value={type} onChange={(event) => setType(parseInt(event.target.value,10))}>
              <option key="Fan" value={1}>Fan</option>
              <option key="Scorer" value={2}>Scorer</option>
              <option key="Coach" value={3}>Coach/Admin</option>
            </select>
          </Col>
        </Row>
        <Row>
          <Col>
            <label>Set Expiration Date (mm/dd/yyyy):</label>
          </Col>
          <Col>
            <input value={date} onChange={event => setDate(event.target.value)}></input>
          </Col>
        </Row>
        <Row>
          <Button onClick={() => addFunc(constructNewAccess())}>Add Fan</Button>
        </Row>
      </Container>
    </div>

  );


}

export default FanAdder;
