import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

import ButtonArea from "./ButtonArea";
import AddDeletePerson from "./AddDeletePerson";
import EventHolder from "./EventHolder";
import StatsSection from "./StatsSection";
import classes from "./TeamPage.module.css";



const TeamPage = ({ state, teamID, access, returnHome }) => {
  const [teamMode, setTeamMode] = React.useState("Home");
  //SET USE EFFECT TO GET PRIMARY TEAM AND SET TEAM MODE;
  useEffect(() => {
    const fetchTeam = () => {
      fetch("/api/teams")
        .then((response) => {
          if (response.ok) {
            return response.json();
          }
          throw new Error(response.statusText);
        }).then((fetchedTeams) => {
          // Create a copy of the collections, replacing the current article if this is an edit
          const myTeam = fetchedTeams.filter(team => team.id == teamID);
          setState(myState);
          setMode(myState.appMode);
          return myState;
        })
    }


    fetchTeam();
//add back CurrUser
}, [state]);

  console.log(team.name.toLowerCase());

  const teamLogo = (
    <div>
      <img src={require(`../assets/images/${team.name.toLowerCase()}.png`)} alt="MyLogo" class="center" />
    </div>
  );

  const typeChecker = (num) => {
    if (num == 3) {
      return "Fan";
    }
    else if (num == 2) {
      return "Scorer";
    }
    else if (num == 1) {
      return "Admin/Coach"
    }
  }

  if (teamMode == "Home") {
    return (
      <div>
        <strong className={classes.AccessLevel}>Access level: {typeChecker(access.type)}</strong>
        <ButtonArea access={access} team={team} back={setTeamMode} returnHome={returnHome} setTeamState={setTeamMode}></ButtonArea>
        {teamLogo}
      </div>
    );
  }
  else if (teamMode == "Game") {
    return (
      <div>
        <EventHolder team={team} back={setTeamMode} userId={access.userId}></EventHolder>
      </div>
    );
  }
  else if (teamMode == "Stat") {
    return (
      <div>
        <StatsSection team={team} back={() => setTeamMode("Home")}></StatsSection>
      </div>
    );
  }
  else {
    return (
      <div>
        <AddDeletePerson team={team} type={teamMode} back={() => setTeamMode("Home")} returnHome={returnHome}></AddDeletePerson>
      </div>
    );
  }




};

export default TeamPage;
