import React, { useState, useEffect } from "react";
import "./App.css";

import styled from "styled-components";
import { Container, Button } from "reactstrap";


const Header = styled.h1`
  background-color: blue;
  text-align: center;
  padding: 1rem;
  color: white;
`;
const ButtonBar = styled.div`
  padding: 0.5rem 0;
  margin-bottom: 0.5rem;
`;

const App = () => {
  const [currUser, setUser] = React.useState(null);
  const [access, setAccess] = React.useState(null);

  useEffect(() => {
      const fetchUser = () => {
        fetch("/api/users")
          .then((response) => {
            if (response.ok) {
              return response.json();
            }
            throw new Error(response.statusText);
          })
          .then((fetchedUsers) => {
            // Create a copy of the collections, replacing the current article if this is an edit
            console.log(fetchedUsers);
            const checkedSignedIn = fetchedUsers.filter(user => user.signedIn == 1);
            if (checkedSignedIn.length != 0) {
              setUser(checkedSignedIn[0]);
            }
          })
          .catch((err) => console.error(err)); // eslint-disable-line no-console
      };

      fetchUser();

    }, []);

  const handleSignInReturn = (username, password) => {


  }
  const handleSignOutReturn = (username) => {

  }


  if (currUser != null) {
    fetch("/api/teamaccess")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((fetchedAccesses) => {
        // Create a copy of the collections, replacing the current article if this is an edit
        const checkedAcc = fetchedAccesses.filter(access => access.userId == currUser.id);
        setAccess(checkedAcc)
      })
      .catch((err) => console.error(err)); // eslint-disable-line no-console
  }

  const teamDisplay = access.map((individual) => <TeamAccessHolder teamaccess={individual}></TeamAccessHolder>);

  if (currUser == null) {
    return (
      <div>
        <Header>Voice-Activated Baseball Scoring</Header>
        <ButtonSelector></ButtonSelector>
        <LoginSquare></LoginSquare>
      </div>
    );
  }


  return (
    <div>
      <Header>Voice-Activated Baseball Scoring</Header>
      <ButtonSelector></ButtonSelector>
      <ul>{teamDisplay}</ul>

    </div>
  );

};

export default App;
