import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";
import classes from './LoginScreen.module.css';

const LoginScreen = ({ signIn, createAccount, errorMessage }) => {

  const [name, setName] = React.useState("");
  const [pword, setPWord] = React.useState("");

  const Title = styled.h2`
    background-color: white;
    text-align: center;
    padding: 1rem;
    color: black;
  `;

  const Message = styled.div`
    background-color: white;
    padding: 70px 0;
    text-align: center;
    color: black;
  `;

  const ButtonHolder = styled.div`
    background-color: black;
    padding: 70px 0;
    text-align: center;
  `;

  const signInButton = (
    <Button
      style={{ backgroundColor: "#0000ff", display: "flex", justifyContent: "center"}}
      onClick={() => {
        signIn(name, pword);
      }}
      >Sign In!</Button>
  );

  const createAccountButton = (
    <Button
      style={{ backgroundColor: "#0000ff", display: "flex", justifyContent: "center"}}
      onClick={() => {
        createAccount(constructNewPerson());
      }}
      >Create Account!</Button>
  );

  const constructNewPerson = () => ({
    username: name,
    password: pword,
    signedIn: 1
  });

  if (errorMessage == "") {
    return (
      <div>
        <Title>Welcome to VABS!</Title>
        <div className={classes.Message}>...voice-activated baseball scorer, that is. Please sign-in with a defined account in order to use the service. If you do not have an account, use the create account button below after filling out the two-fields!</div>
        <div className={classes.MessageContainer}>
          <div className={classes.MessageContainer}>
              <label>Username: </label>
              <input value={name} onChange={event => setName(event.target.value)}></input>
          </div>
          <div className={classes.MessageContainer}>
              <label>Password: </label>
              <input value={pword} onChange={event => setPWord(event.target.value)}></input>
          </div>
        </div>
        <ButtonHolder>
          <Row>
            <Col>
              {signInButton}
            </Col>
            <Col>
              {createAccountButton}
            </Col>
          </Row>
        </ButtonHolder>
      </div>
    );
  }

  return (
    <div>
      <Title>Welcome to VABS!</Title>
      <div className={classes.Message}>...voice-activated baseball scorer, that is. Please sign-in with a defined account in order to use the service. If you do not have an account, use the create account button below after filling out the two-fields!</div>
      <Row>
          <strong className={classes.Error}>{errorMessage}</strong>
      </Row>
      <Row>
        <Col>
          <label>Username: </label>
        </Col>
        <Col>
          <input value={name} onChange={event => setName(event.target.value)}></input>
        </Col>
      </Row>
      <Row>
        <Col>
          <label>Password: </label>
        </Col>
        <Col>
          <input value={pword} onChange={event => setPWord(event.target.value)}></input>
        </Col>
      </Row>
      <ButtonHolder>
        <Row>
          <Col>
            {signInButton}
          </Col>
          <Col>
            {createAccountButton}
          </Col>
        </Row>
      </ButtonHolder>
    </div>
  );



}

export default LoginScreen;
